## given a folder, return all the folder in this folder in a list
def get_folder_list(folder):
    folder_list = []
    for i in os.listdir(folder):
        if os.path.isdir(f"{folder}/{i}"):
            folder_list.append(i)
    print (folder_list)
    # return ['MICA']
    return folder_list


